mapper.update:
UPDATE t_student


        number = #{number}


        name = #{name}


        admin_class = #{adminClass}


        teach_class = #{teachClass}


        obj_score = #{objScore}


        method_score = #{methodScore}


        lexer_score = #{lexerScore}


WHERE id = #{id}

mapper.select:
SELECT stu.name,stu.number,stu.admin_class,stu.teach_class FROM `t_lexer_answer` AS ans LEFT JOIN `t_student` AS stu ON ans.user_id = stu.user_id
ans.is_delete = 0 AND stu.is_delete = 0 AND ans.best_code_id IN
#{id}
